import React, { Component } from "react";
import logo from "../logo.png";
import "./App.css";
import "./logo.css";
import { Web3, web3, ethereum } from "web3";

//This is class component code, it should be changed to functional component code!
class App extends Component {
  async componentWillMount() {
    await this.loadWeb3();
    console.log(window.web3);
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(web3.currentProvider);
    } else {
      window.log(
        "Non-Ethereum browser detected. You should consider trying MetaMask!"
      );
    }
  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-white flex-md-nowrap p-0 shadow">
          <h4>Smart Contracts Business</h4>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <img src={logo} className="App-logo" alt="logo" />
                <h1>Smart Contracts </h1>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
